    var policyPopupBackArrows = document.querySelectorAll('.arrow-left');
    NodeList.prototype.forEach = Array.prototype.forEach;
    console.log(policyButtons);
    policyButtons.forEach(function(pb, index) {
        pb.addEventListener("click", function (e) { openPolicy(index); });
        pb.addEventListener("touchstart", function (e) { openPolicy(index); });
    });
    function openPolicy(index) {
        var popup = policyPopups[index];
        popup.classList.add('visible');
    }
    policyPopupCloseButtons.forEach(function(ppcb) {
        ppcb.addEventListener('click', closePopup);
        ppcb.addEventListener('touchstart', closePopup);
    });
    policyPopupBackArrows.forEach(function(ppca) {
        ppca.addEventListener('click', closePopup);
        ppca.addEventListener('touchstart', closePopup);
    });
    function closePopup(event) {
        var popup = event.currentTarget.parentElement;
        popup.classList.remove('visible');
    }

    
